select op.cod_oportunidade as Oportunidade, sum(opi.qtd) as qtd, op.cod_revenda as revenda, reg.desc_abrev as regi√£o
from at_oportunidade as op left join at_oportunidade_item as opi
on op.cod_oportunidade = opi.cod_oportunidade
left join cd_cliente as cli
on op.cod_revenda = cli.cod_revenda_gerado
left join cd_regiao as reg
on  cli.cod_regiao = reg.cod_regiao

where op.cod_oportunidade in ((7107, 17202, 36302, 41907, 44366, 44492, 44579, 44643, 44647, 44753, 44812, 44839, 44882, 44922, 45658, 46737, 50273, 50885, 50937, 50983, 50999, 51038, 51054, 51116, 51119, 51120, 51126, 51129, 51135, 51150, 51162, 51190, 51199, 51203, 51205, 51210, 51212, 51216, 51217, 51232, 51236, 51242, 51265, 51267, 51613, 53238, 53385, 53454, 53599, 53627, 55331, 59546, 59626, 59628, 59666, 59676, 59678, 59694, 59700, 59703, 59729, 59745, 59752, 59761, 59769, 59773, 59776, 59780, 59792, 59793, 59817, 59820, 59827, 59849, 59854, 59875, 59880, 59882, 59883, 59884, 59888, 59889, 59890, 59898, 59902, 59907, 59910, 59919, 59935, 59937, 59938, 59942, 59948, 59951, 59959, 59960, 59962, 59963, 59964, 59974, 59991, 59992, 60008, 60009)
